config.script:
$.setPortCallback("input",onInput);

function onInput(ctx,s) {
    //$.output(s);

    try {
        var r = JSON.parse(s);
    } catch(e) {
        // fail silently on invalid JSON string
        return;
    }
    if(!r.Success)
        return;
    if(r.Data === null)
        return;

    var out = "";
    for(var i = 0; i < r.Data.length; ++i) {

        if(i === 0)
            out += "speed, battery, rank, user, timest\n" ;

        var row = r.Data[i];
        for(var col in row){
         if(row[col] === 'null')
           row[col] = 0

        }    
            out += row['value.speed'] + ", " +row['value.battery'] +", "+row['value.rank'] +", "+row['value.user'] +", "+row['_MessageGateway_TimeISO8601'] ;
        out += "\n";
    }
    $.output(out);

}
